function create(){stars=[]}function update(){for(var e=0;e<stars.length;e++){var t=stars[e];var n=t.origin.x-t.curr.x;var r=t.origin.y-t.curr.y;var i=Math.sqrt(n*n+r*r);var s=Math.atan2(r,n);var o=s/Math.PI*180;var u=n/i*t.speed;var a=r/i*t.speed;t.curr.x=t.curr.x-u;t.curr.y=t.curr.y-a;t.speed=t.speed+Math.random();t.origin.x=originX;t.origin.y=originY}addNewStars();cleanStars()}function render(){for(var e=0;e<stars.length;e++){var t=stars[e];game.context.fillStyle="rgba("+t.color.red+", "+t.color.green+", "+t.color.blue+", "+t.brightness+")";game.context.fillRect(t.curr.x,t.curr.y,t.size,t.size)}}function addNewStars(){for(var e=0;e<1;e++){var t=(Math.random()*50+1)*Math.cos(Math.PI*Math.round(Math.random()));var n=(Math.random()*50+1)*Math.cos(Math.PI*Math.round(Math.random()));var r=originX+t;var i=originY+n;var s={origin:{x:originX,y:originY},curr:{x:r,y:i},size:2,speed:1.2,slope:(r-originX)/(i-originY),brightness:Math.random(),color:{}};var o=Math.random()*10+1;if(o>9.5)color={red:255,green:170,blue:170};else if(o>9)color={red:170,green:170,blue:255};else if(o>8.7)color={red:255,green:255,blue:153};else color={red:255,green:255,blue:255};s.color=color;stars.push(s)}}function cleanStars(){for(var e=0;e<stars.length;e++){var t=stars[e];if((t.curr.x>WIDTH||t.curr.x<0)&&(t.curr.y>HEIGHT||t.curr.y<0))stars.splice(e,1)}}var WIDTH=window.innerWidth;var HEIGHT=window.innerHeight;var originX=WIDTH/2;var originY=HEIGHT/2;var game=new Phaser.Game(WIDTH,HEIGHT,Phaser.CANVAS,"starsaver",{create:create,update:update,render:render});var stars;$(window).resize(function(){WIDTH=$(window).width();HEIGHT=$(window).height();game.renderer.resize(WIDTH,HEIGHT);originX=WIDTH/2;originY=HEIGHT/2})